class Solution(object):
    def exist(self, board, word):
        def find(i, j, x, di):
            direction = [(0, 1), (1,0), (0, -1), (-1, 0)]
            if ans[0]:
                return True
            for k, d in enumerate(direction):
                if k == di:
                    continue
                if 0 <= x < len(word)-1 and 0 <= i+d[0] < n and 0 <= j+d[1] < m and board[i+d[0]][j+d[1]] == word[x+1] and (i+d[0], j+d[1]) not in _:
                    if x+1 == len(word)-1:
                        ans.pop()
                        ans.append(True)
                        break
                    else:
                        _.append((i+d[0], j+d[1]))
                        find(i+d[0], j+d[1], x+1, (k+2) % 4)
            # if not this path remove the last one index
            _.pop(-1)
            
        ans = [False]
        m = len(board[0])
        n = len(board)
        for i in range(n):
            for j in range(m):
                # check index
                _ = []
                if board[i][j] == word[0]:
                    if len(word) == 1:
                        return True
                    _.append((i,j))
                    find(i, j, 0, 4)
                    if ans[0]:
                        break
        return ans[0]

